.container {
  max-width: 50rem;
  width: 95%;
  margin: 0 auto;
  height: 100%;
}

.quiz {
  height: auto;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  border-radius: 0.5rem;
}

.question {
  box-shadow: rgba(100, 100, 111, 0.2) 0px 6px 10px 0px;
  display: flex;
  align-items: center;
  margin: 0 2rem 2.5rem 2rem;
  font-size: 1.3rem;
  background-color: #ffe286;
  color: #4b3f27;
  border-radius: 0.5rem;
  padding: 3rem 1rem;
  position: relative;
  text-align: center;
}
.question::before {
  content: "?";
  position: absolute;
  top: -4rem;
  right: 1rem;
  transform: rotate(15deg);
  color: #4b3f27;
  font-size: 5rem;
}
.question::after {
  content: "?";
  position: absolute;
  top: -3rem;
  right: -1rem;
  transform: rotate(35deg);
  color: #4b3f27;
  font-size: 4.6rem;
}
.question__stopwatch {
  position: absolute;
  top: 0;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: #4b3f27;
  color: #ffffff;
  border-radius: 50%;
  padding: 0.8rem 4rem;
  box-shadow: rgba(100, 100, 111, 0.2) 0px 6px 10px 0px;
}
.question__stopwatch--danger {
  color: #e44d4d;
  animation: shakeTimer 0.3s linear infinite;
  font-weight: bold;
}
.question__count {
  position: absolute;
  bottom: 0;
  left: 50%;
  transform: translate(-50%, 50%);
  background-color: #ffffff;
  color: #4287b5;
  border-radius: 0.5rem;
  padding: 0.4rem 2rem;
  box-shadow: rgba(100, 100, 111, 0.2) 0px 6px 10px 0px;
}

.answers {
  width: 100%;
  list-style: none;
  margin: 0 auto;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
  background-color: #ffffff;
  border-radius: 0.5rem;
  box-shadow: rgba(100, 100, 111, 0.2) 0px 6px 10px 0px;
  padding: 0.5rem 0;
  margin-top: 1rem;
}

.answer {
  flex-basis: 40%;
  display: flex;
  align-items: center;
  justify-content: center;
  align-self: center;
  background-color: #4287b5;
  border-radius: 0.5rem;
  padding: 0.8rem 0.2rem;
  color: #ffffff;
  margin: 0.5rem 0.5rem;
  min-height: 3.5rem;
  font-size: 0.9rem;
  transition: all 0.1s;
  text-align: center;
}
.answer:hover {
  cursor: pointer;
  background-color: #4287b5;
  color: #ffffff;
  transform: scale(1.05);
}
.answer.success, .answer.success:hover {
  background-color: #42b5a4;
  color: #ffffff;
  transform: scale(1.08);
}
.answer.fail, .answer.fail:hover {
  background-color: #e44d4d;
  color: #ffffff;
  transform: scale(1);
}

.setup-instructions {
  text-align: center;
  margin-bottom: 3rem;
}
.setup-instructions p {
  width: 80%;
  margin: 0 auto;
  font-size: 1.1rem;
}

.setup-form {
  display: flex;
  justify-content: center;
  max-width: 80%;
  flex-wrap: wrap;
}
.setup-form > * {
  flex-basis: 100%;
}
.setup-form__group {
  display: flex;
  flex-direction: column;
  padding: 0.3rem;
  font-size: 1rem;
}
.setup-form__group label {
  margin-right: 0.3rem;
  margin-bottom: 0.5rem;
  width: 100%;
}
.setup-form__group select {
  min-width: 5rem;
}
.setup-form__category-select {
  flex: 1;
}
.setup-form__button {
  margin-top: 1rem;
  cursor: pointer;
  min-width: 10rem;
  background-color: #4287b5;
  color: #ffffff;
  border: 1px solid transparent;
  border-radius: 0.5rem;
  padding: 0.5rem;
  transition: all 0.3s;
}
.setup-form__button:hover {
  cursor: pointer;
  background-color: #4287b5;
  color: #ffffff;
  transform: scaleX(1.05) scaleY(1.2);
}

select {
  border: 1px solid #4287b5;
  padding: 0.35rem;
  color: #4b3f27;
  border-radius: 0.5rem;
}

select::-ms-expand {
  display: none;
}

.help__button {
  margin: 0 0 0.25rem 0;
  font-size: 1.1rem;
  color: #ffffff;
  background-color: #4b3f27;
  padding: 0.5rem 1rem;
  border-radius: 0.5rem;
  cursor: pointer;
  transition: all 0.3s;
}
.help__button:hover {
  cursor: pointer;
  background-color: #4b3f27;
  color: #ffffff;
  transform: scaleX(1.03) scaleY(1.15);
}
.help__button--disabled {
  cursor: initial;
  background-color: #d1d1d1;
}
.help__button--disabled:hover {
  cursor: initial;
  background-color: #d1d1d1;
  transform: scaleX(1.03) scaleY(1.15);
}
.help__answer-removed {
  visibility: hidden;
  pointer-events: none;
}

.final-screen {
  text-align: center;
}
.final-screen p {
  font-size: 1.2rem;
}

