	text-align: center;

	&:hover {
		cursor: pointer;
		background-color: $primary;
		color: $light;
		transform: scale(1.05);
	}

	&.success,
	&.success:hover {
		background-color: $success;
		color: $light;
		transform: scale(1.08);
	}

	&.fail,
	&.fail:hover {
		background-color: $danger;
		color: $light;
		transform: scale(1);
	}
}

